import React,{component} from 'react';
import Axios from 'axios';

const restdetail="http://localhost:8900/restaurantlist";

class SortFilter extends component
{
    constructor(props){
        super(props)
        
    }


     sortFilter=(event)=>
    {
        mealid=parseInt(this.props.mealIdNumber);
        cityid=parseInt(this.props.citiIdNo);
        sort=(event.target.value);
        let url;
        if(event.target.value=='')
        url=`${restdetail}/${cityid}/${mealid}?sort=1`;
        else
        url=`${restdetail}/${cityid}/${mealid}?sort=${sort}`;

        Axios.get(url)
        .then(response=>{this.props.setDataPerSort(response.data)})


    }

    render()
    {
        return(
            <>
            <center>Sort</center>
            <div onChange={this.sortFilter}>
            <input type="radio" id="none" name="sort" value=""/>
            <label for="none">Default </label>
            <input type="radio" id="ascending" name="sort" value="1" onClick={sortFilter(event.target.value)}/>
            <label for="ascending">low to high</label> 
            <br/>
            <input type="radio" id="descending" name="sort" value="-1"/>
            <label for="descending">High to low </label>
            <br/>
            </div>
           
            </>
        )
    }
}


export default SortFilter
